import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import "../App.css";
import firebase from "firebase";
import StyledFirebaseAuth from "react-firebaseui/StyledFirebaseAuth";
import Modal from "react-modal";

const customStyles = {

  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-50%',
    transform             : 'translate(-50%, -100%)'
  }

};

firebase.initializeApp({
  apiKey: "AIzaSyCxiw5em0-MAHTE7v6JJvuXuXpSO7RLOnM",
  authDomain: "usedcarfinder2.firebaseapp.com",
});

class Login extends Component {
  state = { isSignedIn: false, isModalOpen: true };

  uiConfig = {
    signInFlow: "popup",
    signInOptions: [
      firebase.auth.GoogleAuthProvider.PROVIDER_ID,
      // firebase.auth.FacebookAuthProvider.PROVIDER_ID,
      // firebase.auth.GithubAuthProvider.PROVIDER_ID,
      firebase.auth.EmailAuthProvider.PROVIDER_ID,
    ],
    callbacks: {
      signInSuccess: () => false,
    },
  };

  componentDidMount = () => {
    firebase.auth().onAuthStateChanged((user) => {
      this.props.setUser(user);
      this.setState({ isSignedIn: !!user });
      console.log("log in", user);
    });
  };

  render() {
    return (
      <div className="App">
        {this.state.isSignedIn ? (
          <Redirect to="/cars" />
        ) : (
          <div className="Modal">
            <Modal
              isOpen={this.props.open || this.state.isModalOpen}
              // onAfterOpen={afterOpenModal}
              // onRequestClose={closeModal}
              style={customStyles}
              // contentLabel="Example Modal"
            >
              <div className="modal-header">
                <h5>Welcome to React Cars - Please log in</h5>
              </div>

              <StyledFirebaseAuth
                uiConfig={this.uiConfig}
                firebaseAuth={firebase.auth()}
              />
              <div className="modal-footer">
                <button
                  className="btn btn-primary"
                  onClick={() => {
                    this.setState({ isModalOpen: false });
                    this.props.setOpen(false);
                  }}
                >
                  CLOSE
                </button>
              </div>
            </Modal>
          </div>
        )}
      </div>
    );
  }
}

export default Login;
